import React from 'react';
import PropTypes from 'prop-types';

import * as hglib from 'higlass';
import hamradio from 'hamradio'
import './HiglassUI.css'

import 'higlass/build/hglib.css';

import viewconfig from './utils/viewconfig'


class HiglassUI extends React.Component {
  constructor(props) {
    super(props);
    this.api = null

    this.publishLocation = this.publishLocation.bind(this);

    this.state = {
      viewconfig: viewconfig.defaultConfig(props.uid)
    }
    this.subscriptions = this.makeSubscriptions()

    this.prevViewConfig = JSON.stringify(viewconfig.defaultConfig(props.uid))
  }

  shouldComponentUpdate(newProps, newState) {
    if (this.prevViewConfig === JSON.stringify(newState.viewconfig)) {
      return false;
    }

    this.prevViewConfig = JSON.stringify(newState.viewconfig);
    return true;
  }

  componentWillUnmount() {
    this.subscriptions.forEach(sub => sub.unsubscribe())
  }

  publishLocation (location) {
    if (this.props.chromInfo) {
      let chrome = location.xDomain
        .map(coord => this.props.chromInfo.absToChr(coord))
      hamradio.publish('viewport/moved', chrome)
    } else {
      setTimeout(this.publishLocation, 10, location)
    }
  }

  makeSubscriptions() {
    return [
      hamradio.subscribe(
        'viewport/move',
        (name, data) => {
          if (this.props.chromInfo && this.api) {
            let begin = this.props.chromInfo.chrToAbs(data[0])
            let end = this.props.chromInfo.chrToAbs(data[1])
            this.api.zoomTo(this.state.viewconfig.views[1].uid, begin, end, begin, end, 1000)
          }
        }
      ),
      hamradio.subscribe(
        'highlight',
        (name, data) => {
          if (this.props.chromInfo) {
            const highlights = data.map(bounds => bounds.map(coord => this.props.chromInfo.chrToAbs(coord)))
            this.setState({
              viewconfig: viewconfig.setOverlay(this.state.viewconfig, highlights)
            })
          }
        }
      ),
      hamradio.subscribe(
        'track/add', //global channel or focus
        (name, data) => {
          let viewIndex = name.search('global') !== -1 ? 0 : name.search('focus') !== -1 ? 1 : -1
          if (viewIndex !== -1) {
            this.setState({
              viewconfig: viewconfig.addTrack(this.state.viewconfig, viewIndex, data)
            })
          }
        }
      ),
      hamradio.subscribe(
        'track/remove', //global channel or focus
        (name, data) => {
          let viewIndex = name.search('global') ? 0 : name.search('focus') ? 1 : -1
          if (viewIndex !== -1) {
            let trackUID = data.uid || data
            this.setState({
              viewconfig: viewconfig.removeTrack(this.state.viewconfig, viewIndex, trackUID)
            })
          }
        }
      )
    ]
  }

  launchHgLib() {
    return (element) => {
      if (element && this.state.viewconfig) {
        let api = hglib.viewer(
          element,
          this.state.viewconfig,
          { bounded: false }
        );
        this.api = api

        api.on('location', this.publishLocation, this.state.viewconfig.views[1].uid)
      }
    }
  }

  render () {
  	return (
        <div className = "higlass"
          ref={this.launchHgLib()}>
        </div>
		)
  }
}

HiglassUI.propTypes = {
  uid: PropTypes.string,
}

export default HiglassUI;
