import React from 'react'

export default function LabelLayout (props) {
  const divStyle = {
    float: "right",
    minWidth: "50%"
  }
  const labelStyle = {
    marginTop: "5px",
    marginBottom: "5px",
    padding: "1px",
    fontWeight: "700",
    maxWidth: "50%",
    display: "inline-block"
  }
  return (
    <div>
      <label style={labelStyle}>{props.label}</label>
      <div style={divStyle}>{props.inner}</div>
    </div>
  )
}
