import React from 'react'

export default function TextInput (props) {
    let validate = props.validate || (() => true)
    const inputStyle = {
      minWidth: "100px"
    }
    const inputInvalid = Object.assign({
      backgroundColor: "lightred"
    }, inputStyle)
    return (
      <input type="text"
        style={validate(props.value) ? inputStyle : inputInvalid}
        value={props.value}
        onChange={e => props.update(e.target.value)}
      />
    )
}
