export default {
	// retrieve ViewUID (for bottom view)
	fetchViewConfig: function () { // this returns a promise, but no longer needs to afaict. All of this should definitely be replaced by pubsub, though
		return JSON.parse(window.hgApi.exportAsViewConfString()).views[1].uid
	},
	// retrieve current Location (ViewUID is found)
	fetchLocation: function() {
		return JSON.parse(window.hgApi.exportAsViewConfString()).views[1].initialXDomain
	}
}
